import React from "react";
import { buyIceCream } from "../redux/iceCreams/iceCreamActions";
import { connect } from "react-redux";

const IceCreams = ({ numOfIceCreams, buyIceCream }) => {
  return (
    <div>
      <h2>Number of icecreams: {numOfIceCreams}</h2>
      <button onClick={buyIceCream}>Buy icecream</button>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    numOfIceCreams: state.iceCream.numOfIceCreams,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    buyIceCream: () => dispatch(buyIceCream()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(IceCreams);
